                     (record.Travel || 0) + (record.Incentive || 0) + (record.Allowance || 0);

    // 3. Format currency values
    const formatCurrency = (value) =>
        `$${value.toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,')}`;

